import store from "../store";
import classes from "./Counter.module.css";
import { useSelector, useDispatch } from "react-redux";

const Counter = () => {
  const dispatch = useDispatch();
  const counter = useSelector((state) => state.counter); //sottoscrizione automatica realizzata da Redux a una parte dello store (counter)

  const handleIncrement = () => {
    dispatch({ type: "increment" });
  };
  const handleDecrement = () => {
    dispatch({ type: "decrement" });
  };

  const toggleCounterHandler = () => {};

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{counter}</div>
      <div className="buttonsContainer">
        <button className="counterButtons" onClick={handleIncrement}>
          increment
        </button>
        <button className="counterButtons" onClick={handleDecrement}>
          decrement{" "}
        </button>
        <button className="counterButtons" onClick={toggleCounterHandler}>
          Toggle Counter
        </button>
      </div>
    </main>
  );
};

export default Counter;
