import store from "../store";
import classes from "./Counter.module.css";
import { useSelector } from "react-redux";

const Counter = () => {
  const counter = useSelector((state) => state.counter); //sottoscrizione automatica realizzata da Redux a una parte dello store (counter)

  const handleIncrement = () => {
    store.useDispatch (counter.action.increment())
  }

  const toggleCounterHandler = () => {};

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{counter}</div>
      <div className="counterButtons">
        <button onClick={handleIncrement}> increment </button>
        <button onClick={handleIncrement}>decrement </button>
      </div>

      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
