import { createStore } from "redux";
import {createSlice} from "@reduxjs/toolkit"


const initialState = {
  counter: 0,
  showCounter: true,
};

const counterSlice = createSlice({
    name: 'counter',
    initialState: initialState,
    reducers: {
        increment() { },
        decrement() {}
    }

})

const store = createStore(counterSlice);

export default store;
